from modulos import *

class Funcs():
    def limpa_tela(self):
        self.codigo_entry.delete(0, END)
        self.nome_entry.delete(0, END)
        self.telefone_entry.delete(0, END)
        self.cidade_entry.delete(0, END)
    def conecta_bd(self):
        self.conn = sqlite3.connect("clientes.bd")
        self.cursor = self.conn.cursor()
        print("Conectando ao banco de dados...")
    def desconecta_bd(self):
        self.conn.close()
        print('Desconectou com sucesso')
    def monta_tabelas(self):
        self.conecta_bd()
        # Criar tabela
        self.cursor.execute("""
        CREATE TABLE IF NOT EXISTS clientes(
            codigo INTEGER PRIMARY KEY AUTOINCREMENT,
            nome_cliente VARCHAR(80) NOT NULL,
            telefone_cliente VARCHAR(16),
            cidade_cliente VARCHAR(40) 
        );
        """)
        self.conn.commit()
        print('Banco de dados criado com sucesso.')
        self.desconecta_bd()
    def variaveis(self):
        self.codigo = self.codigo_entry.get()
        self.nome = self.nome_entry.get()
        self.telefone = self.telefone_entry.get()
        self.cidade = self.cidade_entry.get()

    def onDoubleClick(self, event):
        self.limpa_tela()
        self.listaCli.selection()
        for n in self.listaCli.selection():
            col1, col2, col3, col4 = self.listaCli.item(n, 'values')
            self.codigo_entry.insert(END, col1)
            self.nome_entry.insert(END, col2)
            self.telefone_entry.insert(END, col3)
            self.cidade_entry.insert(END, col4)

    def insere_cliente(self):
        self.variaveis()
        if self.nome_entry.get() == '':
            msg = "Digite um nome para o cliente"
            messagebox._show('Cadastro de clientes - Aviso!', msg)
        else:
            self.codigo = self.codigo_entry.get()
            self.nome = self.nome_entry.get()
            self.telefone = self.telefone_entry.get()
            self.cidade = self.cidade_entry.get()

        self.conecta_bd()
        self.cursor.execute("""
            INSERT INTO clientes(nome_cliente, telefone_cliente, cidade_cliente)
            VALUES (?, ?, ?);""", (self.nome, self.telefone, self.cidade))
        self.conn.commit()
        self.desconecta_bd()
        self.limpa_tela()
        self.select_lista()
    def altera_cliente(self):
        self.variaveis()
        self.conecta_bd()
        self.cursor.execute("""UPDATE clientes SET nome_cliente = ?, telefone_cliente = ?, cidade_cliente = ? WHERE codigo = ?""", (self.nome, self.telefone, self.cidade, self.codigo))
        self.conn.commit()
        self.desconecta_bd()
        self.limpa_tela()
        self.select_lista()
    def deleta_cliente(self):
        self.variaveis()
        self.conecta_bd()
        self.cursor.execute("""DELETE FROM clientes WHERE codigo = ?""", (self.codigo))
        self.conn.commit()
        self.desconecta_bd()
        self.limpa_tela()
        self.select_lista()

    def select_lista(self):
        self.listaCli.delete(*self.listaCli.get_children())
        self.conecta_bd()
        lista = self.cursor.execute("""
            SELECT codigo, nome_cliente, telefone_cliente, cidade_cliente FROM clientes
            ORDER BY nome_cliente ASC;""")
        for i in lista:
            self.listaCli.insert("", END, values=i)
        self.desconecta_bd()
    def busca_cliente(self):
        self.conecta_bd()
        self.listaCli.delete(*self.listaCli.get_children())

        self.nome_entry.insert(END, '%')
        nome = self.nome_entry.get()
        self.cursor.execute(
            """
            SELECT codigo, nome_cliente, telefone_cliente, cidade_cliente FROM clientes
            WHERE nome_cliente LIKE '%s' ORDER BY nome_cliente ASC
            """ % nome)
        buscanomeCli = self.cursor.fetchall()
        for i in buscanomeCli:
            self.listaCli.insert('', END, values=i)
        self.limpa_tela()
        self.desconecta_bd()
    def images_base64(self):
        # Empacotador de imagens
        self.btnovo_base64 = ''